import{_}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as s,c,a as e,b as o,e as n}from"./app-9e8a13db.js";const i={},l=e("h1",{id:"_2023-07-01-你好生活",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_2023-07-01-你好生活","aria-hidden":"true"},"#"),o(" 2023-07-01 你好生活")],-1),a=e("p",null,"我有多久没有看过星空了，似乎自从离开了中国之后就再也没有看过。国外的夜晚都是担惊受怕地不敢出门，哪里有什么心情去看什么夜空呢？",-1),d=e("p",null,"我还记得，在西工大晚自习完走出星D，沿着门口的那条路走向星操，再转向向北，走向学院楼，在无人的夜空下转向向南，穿过图书馆下的回廊走回宿舍。整条路上虽然只有我一个人，两边都是树林或者山丘，但我始终觉得安心，那时可能不觉得，但现在才反应过来那是一种国家强盛，人民安居乐业的安全感。",-1),h=e("p",null,"我有多久没有想今天这样，真正地在思考自己的生活了。总是得过且过，焦虑地度过每一天，半年前想着作业有没有做完，课程有没有预习，半年后想着毕设怎么进行下去，能不能按时毕业。我总是被生活匆匆推着向前走，从未回头思考一下，我的生活是什么，我想要的生活是什么。",-1),p={href:"https://www.bilibili.com/video/BV19V4y1y73J/?spm_id_from=333.1007.top_right_bar_window_history.content.click&vd_source=dba5caead1bd45d91c4ca15c927c6545",target:"_blank",rel:"noopener noreferrer"},m={href:"https://www.bilibili.com/bangumi/play/ss30759?spm_id_from=333.1007.top_right_bar_window_history.content.click",target:"_blank",rel:"noopener noreferrer"},u=e("p",null,"刚刚看的时候不觉得，但现在才反应过来，为什么我们一旦谈起生活，第一反应都是走向田野走向远方？仿佛身边的生活都是一团乱麻，能走向生活的都是诗和远方。但转念回来也是，日常中鸡毛蒜皮的事洒满一地，工作学习上的，家庭人际上的，地板脏了要扫，水池里的碗要洗。无论是谁体验了那种走进不用自己打扫的精装民宿，采摘不用自己种植的菜田，用冰箱里不用自己准备的调料做饭菜，之后还不需要自己洗碗的日子，都不得大叫一声这才是生活啊！但这真正是生活吗？这只是对于日常生活的逃避罢了，真正需要体验生活，还得拥有撒贝宁那种面对生活的态度。",-1),f=e("p",null,"在尼格给撒贝宁布置了小王子的床单后，小撒的第一反应是“好刻意，但这是我除了在家中最安宁的一个夜晚”和小撒在节目中那种拌嘴接梗的形象，和他认真观星的行为，都能看出他身上的那种无论未来是什么，我都能好好体验我的生活的那种感觉。那是一种我准备好了天文望远镜，等待着未来能够有观星的机会的那种从容，那种对未来的从容不迫的展望是我所羡慕并希望能达到的。而想要达到那种理想，首先要具备的就是认清自己现在的生活，并对未来充满期待。",-1),b=e("p",null,"我想要的生活是什么呢？我对未来的期待是什么呢？今天就到这吧，明天再让我仔细思考一下吧。",-1),w=e("p",null,[o("今日是第一次写长日记，以后也希望能坚持下去，就用小姐姐和吃饭搭子的四字对话结尾吧："),e("strong",null,"所言极是，拭目以待"),o("。")],-1);function k(x,g){const t=r("ExternalLinkIcon");return s(),c("div",null,[l,a,d,h,e("p",null,[o("今天看了"),e("a",p,[o("饼叔的一期读评论"),n(t)]),o("，除了小姐姐很漂亮外，我发现了我渴望的我未来的样子。我特别喜欢片尾的镜子反射调焦构造的蒙太奇的镜头，那是我渴望的表达情感和情绪的模样。那种拥有相当阅历，能大开大合，能见微知著的那种渊博形象。我按照视屏中那一闪而过的电影海报集查起了电影，想通过电影体验不一样的人生，获取摄影和音乐的技能，毕竟我身上并没有艺术细菌，但却意外地发现了"),e("a",m,[o("《你好生活》"),n(t)]),o("这部综艺。这里，才真真正正体现出一种接得住，拿得起的知识青年的形象，毕竟那两个人叫撒贝宁和尼格买提。当然哈哈还有两个经常接不住的吕子乔和董什么。")]),u,f,b,w])}const V=_(i,[["render",k],["__file","2023-07-01 你好生活.html.vue"]]);export{V as default};
